import React from "react";
import { useEffect, useState } from "react";

export default function Library({ novels = [], onDelete }) {
    const [error, setError] = useState(null);

    const fetchLibrary = async () => {
        try {
            const res = await fetch(`http://localhost:8080/api/novels`);
            if(!res.ok) throw new Error(`Failed to fetch library (${res.status})`);
            const data = await res.json();
            setLibrary(data || []);
        } catch (err) {
            console.error("Could not load library: ", err);
            setLibrary([]);
        }
    };

    const handleRemove = async (id) => {
        if(!id) return;
        try {
            const res = await fetch(`http://localhost:8080/api/novels/${id}`, {
                method: "DELETE",
            });
            if(!res.ok) {
                const text = await res.text();
                throw new Error(`Delete failed: ${res.status} ${text}`);
            }
            setLibrary(prev => prev.filter(item => item.id !== id));
            alert("Removed from library!");
        } catch (err) {
            console.error("Delete failed: ", err);
            alert("Failed to remove novel: " + (err.message || err));
        }
    };

